import request from './request';

// 预约API
const appointmentAPI = {
  // 获取预约列表（管理员用）
  getAppointments: (params = {}) => {
    return request({
      url: '/appointment/list',
      method: 'get',
      params: params
    });
  },

  // 批量确认预约
  batchConfirmAppointments: (appointmentIds) => {
    return request({
      url: '/appointment/batch/confirm',
      method: 'put',
      data: { ids: appointmentIds }
    });
  },

  // 批量取消预约
  batchCancelAppointments: (appointmentIds, reason = '') => {
    return request({
      url: '/appointment/batch/cancel',
      method: 'put',
      data: { ids: appointmentIds, reason: reason }
    });
  },
  // 创建预约
  createAppointment: (data) => {
    return request({
      url: '/appointment',
      method: 'post',
      data: data
    });
  },

  // 获取患者预约列表
  getPatientAppointments: (patientId) => {
    return request({
      url: '/appointment/patient/' + patientId,
      method: 'get'
    });
  },

  // 获取医生预约列表
  getDoctorAppointments: (doctorId) => {
    return request({
      url: '/appointment/doctor/' + doctorId,
      method: 'get'
    });
  },

  // 取消预约
  cancelAppointment: (appointmentId, reason) => {
    return request({
      url: '/appointment/' + appointmentId + '/cancel',
      method: 'put',
      data: { reason: reason }
    });
  },

  // 更新预约状态
  updateAppointmentStatus: (appointmentId, status) => {
    return request({
      url: '/appointment/' + appointmentId + '/status',
      method: 'put',
      data: { status: status }
    });
  },

  // 查询医生排班
  getDoctorSchedule: (doctorId, date) => {
    return request({
      url: '/appointment/schedule',
      method: 'get',
      params: { doctorId: doctorId, date: date }
    });
  },

  // 检查时间段可用性
  checkTimeSlotAvailability: (doctorId, appointmentDate, timeSlot) => {
    return request({
      url: '/appointment/check-availability',
      method: 'get',
      params: {
        doctorId: doctorId,
        appointmentDate: appointmentDate,
        timeSlot: timeSlot
      }
    });
  },

  // 获取预约详情
  getAppointmentDetail: (appointmentId) => {
    return request({
      url: '/appointment/' + appointmentId,
      method: 'get'
    });
  }
};

export default appointmentAPI;
