import request from './request'

const patientAPI = {
  // 获取患者信息
  getPatientInfo() {
    return request({
      url: '/patient/info',
      method: 'GET'
    })
  },

  // 获取患者信息根据ID
  getPatientById(id) {
    return request({
      url: `/patient/${id}`,
      method: 'GET'
    })
  },

  // 更新患者信息
  updatePatient(data) {
    return request({
      url: '/patient/update',
      method: 'PUT',
      data: data
    })
  },

  // 患者注册
  addPatient(patientData) {
    return request({
      url: '/patient/register',
      method: 'POST',
      data: patientData
    })
  },

  // 根据用户名获取患者信息
  getPatientByUsername(username) {
    return request({
      url: `/patient/username/${username}`,
      method: 'GET'
    })
  }
}

export default patientAPI
