import request from './request'

const patientAPI = {
  // 获取患者列表
  getAllPatients() {
    return request({
      url: '/patients',
      method: 'GET'
    })
  },

  // 根据ID获取患者详情
  getPatientById(id) {
    return request({
      url: `/patients/${id}`,
      method: 'GET'
    })
  },

  // 添加患者
  addPatient(patientData) {
    return request({
      url: '/patients',
      method: 'POST',
      data: patientData
    })
  },

  // 更新患者信息
  updatePatient(id, patientData) {
    return request({
      url: `/patients/${id}`,
      method: 'PUT',
      data: patientData
    })
  },

  // 删除患者
  deletePatient(id) {
    return request({
      url: `/patients/${id}`,
      method: 'DELETE'
    })
  },

  // 批量删除患者
  batchDeletePatients(ids) {
    return request({
      url: '/patients/batch',
      method: 'DELETE',
      data: { ids }
    })
  },

  // 搜索患者
  searchPatients(searchParams) {
    return request({
      url: '/patients/search',
      method: 'GET',
      params: searchParams
    })
  },

  // 获取患者预约记录
  getPatientAppointments(patientId) {
    return request({
      url: `/patients/${patientId}/appointments`,
      method: 'GET'
    })
  }
}

export default patientAPI
