import request from './request';

// 科室API
const departmentAPI = {
  // 获取所有科室列表
  getAllDepartments: () => {
    return request({
      url: '/department/list',
      method: 'get'
    });
  },

  // 获取科室详情
  getDepartmentDetail: (deptId) => {
    return request({
      url: '/department/' + deptId,
      method: 'get'
    });
  },

  // 添加科室
  addDepartment: (data) => {
    return request({
      url: '/department',
      method: 'post',
      data: data
    });
  },

  // 更新科室信息
  updateDepartment: (deptId, data) => {
    return request({
      url: '/department/' + deptId,
      method: 'put',
      data: data
    });
  },

  // 更新科室状态
  updateDepartmentStatus: (deptId, status) => {
    return request({
      url: '/department/' + deptId + '/status',
      method: 'put',
      data: { status: status }
    });
  },

  // 删除科室
  deleteDepartment: (deptId) => {
    return request({
      url: '/department/' + deptId,
      method: 'delete'
    });
  },

  // 获取科室医生列表
  getDepartmentDoctors: (deptId) => {
    return request({
      url: '/department/' + deptId + '/doctors',
      method: 'get'
    });
  }
};

export default departmentAPI;
