import request from './request';

// 医疗导诊API
const medicalGuideAPI = {
  // 获取医疗导诊记录
  getGuidanceRecords: (patientId) => {
    return request({
      url: '/medical-guidance/patient/' + patientId,
      method: 'get'
    });
  },

  // 创建医疗导诊记录
  createGuidance: (data) => {
    return request({
      url: '/medical-guidance',
      method: 'post',
      data: data
    });
  },

  // 获取科室推荐
  getDepartmentRecommendation: (symptoms) => {
    return request({
      url: '/medical-guidance/recommend',
      method: 'post',
      data: { symptoms: symptoms }
    });
  },

  // 获取导诊详情
  getGuidanceDetail: (guidanceId) => {
    return request({
      url: '/medical-guidance/' + guidanceId,
      method: 'get'
    });
  },

  // 更新导诊状态
  updateGuidanceStatus: (guidanceId, status) => {
    return request({
      url: '/medical-guidance/' + guidanceId + '/status',
      method: 'put',
      data: { status: status }
    });
  },

  // 获取常见科室信息
  getCommonDepartments: () => {
    return request({
      url: '/medical-guidance/common-departments',
      method: 'get'
    });
  },

  // 获取健康小贴士
  getHealthTips: () => {
    return request({
      url: '/medical-guidance/health-tips',
      method: 'get'
    });
  }
};

export default medicalGuideAPI;
